mapper.delete:
DELETE FROM board
WHERE board_no = #{boardNo}
AND member_id = #{memberId}

DELETE FROM comment
WHERE member_id = #{memberId}
AND board_no = #{boardNo}
AND comment_no = #{commentNo}

mapper.update:
UPDATE board
SET 
	board_title = #{boardTitle},
	board_content = #{boardContent}
WHERE board_no = #{boardNo}
AND member_id = #{memberId}

mapper.select:
SELECT
	board_title boardTitle,
	board_content boardContent
FROM board
WHERE member_id = #{memberId}
AND board_no = #{boardNo}
